import React from "react";
import { useState } from "react";

function Navbar() {
  // State to track whether the menu bar is visible or not
  const [isMenuShowed, setIsMenuShowed] = useState(false);

  // Function to toggle the menu visibility
  const toggleMenu = () => {
    setIsMenuShowed(!isMenuShowed);
  };

  const closeMenu = () => {
    setIsMenuShowed(false);
  };
  return (
    //The NavBar
    <nav className="flex flex-col">
      <div className="flex flex-row justify-between">
        <h4 className="text-3xl">ProMech</h4>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={1.5}
          stroke="currentColor"
          className="w-9 h-9 nav"
          onClick={toggleMenu}
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"
          />
        </svg>
      </div>

      {isMenuShowed && (
        <div className="flex flex-col ml-[68%] text-xl bg-deep-blue text-pure-white items-center py-6 h-full">
          <div className="ml-auto">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-9 h-9 nav"
              onClick={closeMenu}
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </div>
          <div className="">
            <ul>
              <li>
                <a href="/home">Home</a>
              </li>
              <li>
                <a href="/profile">Profile</a>
              </li>
              <li>
                <a href="/">About Us</a>
              </li>
              <li>
                <a href="/login">Login</a>
              </li>
              <li>
                <a href="/signup-user">Sign Up</a>
              </li>
            </ul>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
